INSERT INTO public.assessment (assessment_name, min_score, max_score, result_description, test_id) VALUES ('Выраженная склонность к рискованному поведению', 20, 50, 'Высокая готовность к риску сопровождается низкой мотивацией к избеганию неудач (защитой). Готовность к риску достоверно связана прямо пропорционально с числом допущенных ошибок. Исследования дали также следующие результаты: - с возрастом готовность к риску падает; - у более опытных работников готовность к риску ниже, чем у неопытных; - у женщин готовность к риску реализуется при более определенных условиях, чем у мужчин; - у военных командиров и руководителей предприятий готовность к риску выше, чем у студентов; - с ростом отверженности личности, в ситуации внутреннего конфликта растет готовность к риску; - в условиях группы готовность к риску проявляется сильней, чем при действиях в одиночку, и зависит от групповых ожиданий.', 1);
INSERT INTO public.assessment (assessment_name, min_score, max_score, result_description, test_id) VALUES ('Умеренная склонность к риску', 11, 19, 'Высокая готовность к риску сопровождается низкой мотивацией к избеганию неудач (защитой). Готовность к риску достоверно связана прямо пропорционально с числом допущенных ошибок. Исследования дали также следующие результаты: - с возрастом готовность к риску падает; - у более опытных работников готовность к риску ниже, чем у неопытных; - у женщин готовность к риску реализуется при более определенных условиях, чем у мужчин; - у военных командиров и руководителей предприятий готовность к риску выше, чем у студентов; - с ростом отверженности личности, в ситуации внутреннего конфликта растет готовность к риску; - в условиях группы готовность к риску проявляется сильней, чем при действиях в одиночку, и зависит от групповых ожиданий.', 1);
